import React from "react";
import useStyles from './styles';
import { Card, CardActions, CardContent, CardMedia, Button, Typohraphy } from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from  '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icon/MoreHoriz';
import moment from 'moment';

const Post = ({ post }) => {
    const classes = useStyles();
    return (
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={post.selectedFile} title={post.title} />
            <div className={classes.overlay}>
                <Typohraphy variant="h6">{post.creator}</Typohraphy>
                <Typohraphy variant="body2">{moment(post.createdAt).fromNow()}</Typohraphy>
            </div>
            <div className={classes.overlay2}>
                <Button style={{color: 'white'}} size="small" onClick={() => {}} >
                    <MoreHorizIcon fontSize="default" />
                </Button>
            </div>
            <div className={classes.details}>
                <Typohraphy variant="body2" color="textSecondary">{post.tags.map()}</Typohraphy>
            </div>
        </Card>
    )
}

export default Post;
